import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import LoginPage from './pages/LoginPage';
import SignupPage from './pages/SignupPage';
import TravelPlannerPage from './pages/TravelPlannerPage';
import '@fontsource/inter';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    background: {
      default: '#f7f9fb',
    },
  },
  typography: {
    fontFamily: 'Inter, Arial, sans-serif',
    h2: { fontWeight: 700 },
    h3: { fontWeight: 600 },
  },
  shape: {
    borderRadius: 14,
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <AppBar position="static" color="inherit" elevation={1} sx={{ mb: 4 }}>
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 700, color: '#1976d2' }}>
              Indian Offbeat Travel Guide
            </Typography>
            <Button color="primary" component={Link} to="/login">Login</Button>
            <Button color="primary" component={Link} to="/signup">Signup</Button>
            <Button color="primary" component={Link} to="/planner">Travel Planner</Button>
          </Toolbar>
        </AppBar>
        <Box sx={{ minHeight: 'calc(100vh - 64px)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <Routes>
            <Route path="/login" element={<LoginPage />} />
            <Route path="/signup" element={<SignupPage />} />
            <Route path="/planner" element={<TravelPlannerPage />} />
            <Route path="/" element={<Box textAlign="center"><Typography variant="h2" gutterBottom>Welcome to Indian Offbeat Travel Guide</Typography><Typography variant="h5">Discover hidden gems, treks, and local flavors across India.</Typography></Box>} />
          </Routes>
        </Box>
      </Router>
    </ThemeProvider>
  );
}

export default App;
