const palette = {
  primary: {
    main: '#FF6B35', // Deep saffron orange
    contrastText: '#fff',
  },
  secondary: {
    main: '#1E3A8A', // Vibrant blue
    contrastText: '#fff',
  },
  success: {
    main: '#228B22', // Forest green
    contrastText: '#fff',
  },
  warning: {
    main: '#CD853F', // Warm terracotta
    contrastText: '#fff',
  },
  background: {
    default: '#FFF8DC', // Soft cream (top of gradient)
    paper: '#FAFAFA', // Soft white for cards
    gradient: 'linear-gradient(to bottom, #FFF8DC 0%, #FFEAA7 100%)',
  },
  text: {
    primary: '#2C3E50', // Charcoal gray
    secondary: '#555',
  },
  accent: {
    main: '#FFD700', // Bright marigold
  },
};

export default palette;
